
import { EventEmitter, Injectable } from '@angular/core';
import { Subject } from 'rxjs';
//import { Subject } from 'rxjs';

import { Ingredient } from './../shared/ingredient.model';
import { Recipe } from './recipes.model';
//import { Ingredient } from './../shared/ingredient.model';
import { ShoppingListService } from './../shopping-list/shopping-list.service';

@Injectable()

export class RecipeService{

  recipeChanges = new Subject<Recipe[]>();

  //recipeSelected=new EventEmitter<Recipe>();
  //recipeSelected=new Subject<Recipe>();

    private recipes: Recipe[] = [
        new Recipe(
          'A Test Recipe',
          'This is simply a test',
          'data:image/jpeg;base64,'
        , [
            new Ingredient("Meat", 1),
            new Ingredient("French Fries", 20)
        ]),
        new Recipe(
          'Test2',
          'this is test 2 ',
          'data:image/jpeg;base64,'
        , [
            new Ingredient("Buns", 2),
            new Ingredient("Meat", 2)
        ]),
      ]; //the type is an array of recipes

      constructor(private slService:ShoppingListService){}

      getRecipe(){
          //return this.recipes; ** we return the direct ref to this array by this way
          return this.recipes.slice(); // this will return a new array which is a exact copy of the one in this file.
      }

      getRecipee(index : number){
        return this.recipes[index];
      }

      addIngredientsToShoppingList(ingredients:Ingredient[]){
        this.slService.addIngredientsss(ingredients);
      }

      addRecipe(recipe: Recipe){
        this.recipes.push(recipe);
        this.recipeChanges.next(this.recipes.slice());

      }

      updateRecipe(index: number, newRecipe: Recipe){
        this.recipes[index] = newRecipe;
        this.recipeChanges.next(this.recipes.slice());
      }

      deleteRecipe(index: number){
        this.recipes.splice(index,1);
        this.recipeChanges.next(this.recipes.slice());
      }
    
}